import React, { useEffect, useState } from 'react';
import { fetchClasses } from '../../api/api';
import TimezoneFilter from '../TimezoneFilter';
import { bookClass } from '../../api/api';

const ClassesList = ({ user }) => {
    const [classes, setClasses] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [timezone, setTimezone] = useState('Asia/Kolkata'); // Fixed typo: Kolkata not Kolkatha
    const [bookingMsg, setBookingMsg] = useState('');
    const [bookingStatus, setBookingStatus] = useState(''); // 'success' or 'error'

    const handleTimezoneChange = (newTimezone) => {
        setTimezone(newTimezone);
    };

    useEffect(() => {
        const getClasses = async () => {
            setLoading(true);
            setError(null);
            try {
                const data = await fetchClasses(timezone);
                setClasses(data);
            } catch (err) {
                setError(err.message);
            } finally {
                setLoading(false);
            }
        };

        getClasses();
    }, [timezone]); // This ensures API call happens when timezone changes

    const formatDateTime = (isoString) => {
        const options = {
            weekday: 'short',
            year: 'numeric',
            month: 'short',
            day: 'numeric',
            hour: '2-digit',
            minute: '2-digit',
            timeZoneName: 'short',
            timeZone: timezone // Use selected timezone
        };
        return new Date(isoString).toLocaleString(undefined, options);
    };

    const handleBookClass = async (classItem) => {
        setBookingMsg('');
        setBookingStatus('');

        if (!user || !user.name || !user.email) {
            setBookingMsg('Please log in to book a class.');
            setBookingStatus('error');
            return;
        }

        try {
            await bookClass({
                name: user.name,
                email: user.email,
                class_id: classItem.class_id
            });
            setBookingMsg(`Successfully booked ${classItem.name}!`);
            setBookingStatus('success');
        } catch (err) {
            setBookingMsg('Booking failed. Please try again.');
            setBookingStatus('error');
        }
    };

    if (loading) return <div>Loading classes...</div>;
    if (error) return <div>Error: {error}</div>;

    return (
        <div>
            <style>
                {`
                    @keyframes fadeInSlide {
                        0% {
                            opacity: 0;
                            transform: translateY(-10px);
                        }
                        100% {
                            opacity: 1;
                            transform: translateY(0);
                        }
                    }
                `}
            </style>
            <h3>Hi{user && user.name ? `, ${user.name}` : ''}!</h3>
            <TimezoneFilter selectedTimezone={timezone} onTimezoneChange={handleTimezoneChange} />
            <h2>Available Classes</h2>
            {bookingMsg && (
                <div 
                    style={{ 
                        color: bookingStatus === 'success' ? 'green' : 'red',
                        padding: '10px',
                        marginBottom: '10px',
                        borderRadius: '4px',
                        backgroundColor: bookingStatus === 'success' ? '#d4edda' : '#f8d7da',
                        border: `1px solid ${bookingStatus === 'success' ? '#c3e6cb' : '#f5c6cb'}`,
                        animation: 'fadeInSlide 0.5s ease-out',
                        fontWeight: 'bold'
                    }}
                >
                    {bookingMsg}
                </div>
            )}
            <ul>
                {classes.map((classItem) => (
                    <li key={classItem.id} style={{ marginBottom: '1rem' }}>
                        <strong>{classItem.name}</strong><br />
                        Date & Time: {formatDateTime(classItem.datetime)}<br />
                        Instructor: {classItem.instructor}<br />
                        Available Slots: {classItem.available_slots}
                        <br />
                        <button onClick={() => handleBookClass(classItem)}>
                            Book Class
                        </button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default ClassesList;
