import React, { useEffect, useState } from 'react';
import { fetchClasses } from '../../api/api';
import TimezoneFilter from '../TimezoneFilter';

const ClassesList = ({ defaultTimezone }) => {
    const [classes, setClasses] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [timezone, setTimezone] = useState('UTC');

    const handleTimezoneChange = (newTimezone) => {
            setTimezone(newTimezone);
        };
    useEffect(() => {
        const getClasses = async () => {
            try {
                const data = await fetchClasses(defaultTimezone);
                setClasses(data);
            } catch (err) {
                setError(err.message);
            } finally {
                setLoading(false);
            }
        };

        getClasses();
    }, [defaultTimezone]);

    if (loading) {
        return <div>Loading classes...</div>;
    }

    if (error) {
        return <div>Error: {error}</div>;
    }

    return (
        <div>
            <TimezoneFilter onChange={handleTimezoneChange} />
            <h2>Available Classes</h2>
            <ul>
                {classes.map((classItem) => (
                    <li key={classItem.id}>
                        {classItem.name} - {classItem.time} ({classItem.timezone})
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default ClassesList;
