import React from 'react';
import { Link } from 'react-router-dom';

const UserIcon = ({ user }) => {
    const location = useLocation();

    const isHomePage = location.pathname === '/';

    if (!user && isHomePage) {
        return (
            <div className="user-icon">
                <span>User not logged in</span>
                <div className="user-icon__links">
                    <Link to="/login">Login</Link>
                    <Link to="/signup">Signup</Link>
                </div>
            </div>
        );
    }

    if (!user) {
        return null; // Nothing is shown on other pages
    }


    return (
        <div className="user-icon">
            <img src={user.profilePicture} alt="User Icon" className="user-icon__image" />
            <div className="user-icon__details">
                <p className="user-icon__name">{user.name}</p>
                <div className="user-icon__links">
                    <Link to="/user-details">User Details</Link>
                    <Link to="/bookings">Bookings</Link>
                </div>
            </div>
        </div>
    );
};

export default UserIcon;
