import React, { useEffect, useState } from 'react';
import { fetchBookings } from '../../api/api';

const BookingsPage = ({ userEmail, timezone = 'Asia/Kolkata' }) => {
    const [bookings, setBookings] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        const getBookings = async () => {
            setLoading(true);
            setError('');
            try {
                const data = await fetchBookings(userEmail);
                setBookings(data);
            } catch (err) {
                setError('Failed to fetch bookings.');
            } finally {
                setLoading(false);
            }
        };
        if (userEmail) {
            getBookings();
        }
    }, [userEmail]);

    // Example for ClassesList.jsx and BookingsPage.jsx

const formatDateTime = (isoString, timezone) => {
    const date = new Date(isoString);
    return new Intl.DateTimeFormat('en-GB', {
        day: '2-digit',
        month: '2-digit',
        year: 'numeric',
        hour: '2-digit',
        minute: '2-digit',
        second: '2-digit',
        hour12: false,
        timeZone: timezone,
    }).format(date);
};
    if (!userEmail) {
        return <div>Please log in to view your bookings.</div>;
    }

    if (loading) return <div>Loading bookings...</div>;
    if (error) return <div>{error}</div>;

    return (
        <div>
            <h2>Your Bookings</h2>
            {bookings.length === 0 ? (
                <p>No bookings found.</p>
            ) : (
                <ul>
                    {bookings.map((booking) => (
                        <li key={booking.id}>
                            {booking.class_name} - {formatDateTime(booking.datetime,timezone)}
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default BookingsPage;
