import React, { useEffect, useState } from 'react';
import { fetchClasses, bookClass } from '../../api/api';
import TimezoneFilter from '../TimezoneFilter';

const ClassesList = ({ user }) => {
    const [classes, setClasses] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [timezone, setTimezone] = useState('Asia/Kolkata');
    const [bookingMsg, setBookingMsg] = useState('');

    useEffect(() => {
        // Load timezone from localStorage if available
        const savedTimezone = localStorage.getItem('preferredTimezone');
        if (savedTimezone) {
            setTimezone(savedTimezone);
        }
    }, []);

    useEffect(() => {
        const getClasses = async () => {
            setLoading(true);
            setError(null);
            try {
                const data = await fetchClasses(timezone);
                setClasses(data);
            } catch (err) {
                setError(err.message || 'Error fetching classes');
            } finally {
                setLoading(false);
            }
        };

        getClasses();
    }, [timezone]);

    const handleTimezoneChange = (newTimezone) => {
        setTimezone(newTimezone);
        localStorage.setItem('preferredTimezone', newTimezone);
    };

    const formatDateTime = (isoString) => {
        const options = {
            weekday: 'short',
            year: 'numeric',
            month: 'short',
            day: 'numeric',
            hour: '2-digit',
            minute: '2-digit',
            timeZoneName: 'short',
            timeZone: timezone
        };
        return new Date(isoString).toLocaleString(undefined, options);
    };

    const handleBookClass = async (classItem) => {
        setBookingMsg('');
        if (!user || !user.name || !user.email) {
            setBookingMsg('Please log in to book a class.');
            return;
        }
        try {
            await bookClass({
                name: user.name,
                email: user.email,
                class_id: classItem.class_id
            });
            setBookingMsg(`Successfully booked ${classItem.name}!`);
        } catch (err) {
            setBookingMsg('Booking failed. Please try again.');
        }
    };

    if (loading) return <div>Loading classes...</div>;
    if (error) return <div>Error: {error}</div>;

    return (
        <div>
            <h3>Hi{user && user.name ? `, ${user.name}` : ''}!</h3>
            <TimezoneFilter onChange={handleTimezoneChange} value={timezone} />
            <h2>Available Classes</h2>
            {bookingMsg && <div style={{ color: 'green' }}>{bookingMsg}</div>}
            <ul>
                {classes.map((classItem) => (
                    <li key={classItem.id} style={{ marginBottom: '1rem' }}>
                        <strong>{classItem.name}</strong><br />
                        Date & Time: {formatDateTime(classItem.datetime)}<br />
                        Instructor: {classItem.instructor}<br />
                        Available Slots: {classItem.available_slots}<br />
                        <button onClick={() => handleBookClass(classItem)}>
                            Book Class
                        </button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default ClassesList;
