import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const UserIcon = ({ user }) => {
    const location = useLocation();

    const isHomePage = location.pathname === '/';
    const isClassesPage = location.pathname === '/classes';

    if (!user && isHomePage) {
        return (
            <div className="user-icon">
                <div className="user-icon__links">
                    <Link to="/login">Login</Link>
                    <Link to="/signup">Signup</Link>
                </div>
            </div>
        );
    }

    if (!user) {
        return null; // Nothing is shown on other pages
    }

   return (
    <div 
        className="user-icon__links"
        style={{
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            gap: '2rem',
            flexWrap: 'wrap',
            padding: '1rem 0'
        }}
    >
        {/* Show Bookings link only on /classes */}
        {isClassesPage && (
            <Link 
                to="/bookings"
                style={{
                    color: '#a0d9ef',
                    textDecoration: 'none',
                    position: 'relative',
                    transition: 'all 0.3s ease',
                    fontWeight: '500',
                    fontSize: '1.1rem',
                    padding: '0.5rem 1rem',
                    borderRadius: '8px',
                    background: 'rgba(160, 217, 239, 0.1)',
                    border: '1px solid rgba(160, 217, 239, 0.2)',
                    backdropFilter: 'blur(10px)'
                }}
                onMouseEnter={(e) => {
                    e.target.style.color = '#ffffff';
                    e.target.style.textShadow = '0 0 10px rgba(160, 217, 239, 0.8)';
                    e.target.style.background = 'rgba(160, 217, 239, 0.2)';
                    e.target.style.transform = 'translateY(-2px)';
                    e.target.style.boxShadow = '0 5px 15px rgba(160, 217, 239, 0.3)';
                }}
                onMouseLeave={(e) => {
                    e.target.style.color = '#a0d9ef';
                    e.target.style.textShadow = 'none';
                    e.target.style.background = 'rgba(160, 217, 239, 0.1)';
                    e.target.style.transform = 'translateY(0)';
                    e.target.style.boxShadow = 'none';
                }}
            >
                Bookings
            </Link>
        )}
    </div>
);
};

export default UserIcon;
